mapper.select:
select
sm.*
from sys_module sm, sys_role_module srm
where sm.id = srm.module_id

    and sm.type_code = #{typeCode,jdbcType=VARCHAR}

and srm.role_id = #{roleId,jdbcType=BIGINT}

select sm.* from sys_module sm, sys_role_module srm, sys_role_user sru where sm.id = srm.module_id and srm.role_id = sru.role_id
and sm.type_code = #{typeCode,jdbcType=VARCHAR}
and sru.user_id = #{userId,jdbcType=BIGINT}

select
  sm.*
from sys_module sm, sys_role_module srm
where sm.id = srm.module_id

  and sm.type_code = #{typeCode,jdbcType=VARCHAR}

and srm.role_id = #{roleId,jdbcType=BIGINT}

select sm.* from sys_module sm, sys_role_module srm, sys_role_user sru where sm.id = srm.module_id and srm.role_id = sru.role_id
and sm.type_code = #{typeCode,jdbcType=VARCHAR}
and sru.user_id = #{userId,jdbcType=BIGINT}
